// src/Cars.jsx

import React, { useContext } from 'react';
import CarsContext from './context/CarsContext';
import carBlue from './images/carBlue.jpeg';
import carRed from './images/carRed.jpeg';
import carYellow from './images/carYellow.jpeg';

function Cars() {
    const { red, setStateRed, blue, setStateBlue, yellow, setStateYellow } = useContext(CarsContext);
    const { moveCar } = this.context;
    return (
      <div>
        <div>
          <img
            className={red ? 'car-right' : 'car-left'}
            src={carRed}
            alt="red car"
          />
          <button
            onClick={() => setStateRed( !red)}
            type="button"
          >
            Move
        </button>
        </div>
        <div>
          <img
            className={blue ? 'car-right' : 'car-left'}
            src={carBlue}
            alt="blue car"
          />
          <button
            onClick={() => setStateBlue(!blue)}
            type="button"
          >
            Move
        </button>
        </div>
        <div>
          <img
            className={yellow ? 'car-right' : 'car-left'}
            src={carYellow}
            alt="yellow car"
          />
          <button
            onClick={() => setStateYellow( !yellow)}
            type="button"
          >
            Move
        </button>
        </div>
      </div>
    )
};

Cars.contextType = CarsContext;

export default Cars;
